import { useState, useContext } from 'react';
import { AuthContext } from '../context/AuthContext';
import { useNavigate, Link } from 'react-router-dom';
import axios from 'axios';
import Navbar from '../components/Navbar';
import { Eye, EyeOff } from 'lucide-react'; // Optional: Install lucide-react for icons

export default function Login() {
  const { login } = useContext(AuthContext);
  const navigate = useNavigate();
  const [form, setForm] = useState({ email: '', password: '' });
  const [error, setError] = useState('');
  const [showPassword, setShowPassword] = useState(false);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    try {
      const res = await axios.post('http://localhost:5000/api/auth/login', form);
      const { token, user } = res.data;
      login(token, user);
      navigate('/');
    } catch (err) {
      setError(err.response?.data?.msg || 'Login failed');
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-indigo-100 via-purple-100 to-pink-100 flex flex-col items-center justify-center px-4">
      <Navbar />

      <form
        onSubmit={handleSubmit}
        className="bg-white p-8 rounded-3xl shadow-2xl w-full max-w-md mt-12 border border-purple-100"
        noValidate
      >
        <h2 className="text-3xl font-extrabold text-center mb-6 text-purple-700">üîê Sign In</h2>

        {error && (
          <p className="bg-red-100 text-red-700 px-4 py-2 mb-4 rounded text-sm border border-red-300 text-center">
            {error}
          </p>
        )}

        <div className="mb-5">
          <label className="block text-sm font-semibold text-gray-700 mb-1">Email</label>
          <input
            type="email"
            name="email"
            value={form.email}
            onChange={handleChange}
            placeholder="you@example.com"
            className="w-full border border-gray-300 rounded-lg px-4 py-2 transition focus:outline-none focus:ring-2 focus:ring-purple-400"
            required
          />
        </div>

        <div className="mb-6 relative">
          <label className="block text-sm font-semibold text-gray-700 mb-1">Password</label>
          <input
            type={showPassword ? 'text' : 'password'}
            name="password"
            value={form.password}
            onChange={handleChange}
            placeholder="‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢"
            className="w-full border border-gray-300 rounded-lg px-4 py-2 pr-10 transition focus:outline-none focus:ring-2 focus:ring-purple-400"
            required
          />
          <button
            type="button"
            onClick={() => setShowPassword(prev => !prev)}
            className="absolute right-3 top-9 text-gray-600 hover:text-purple-600"
          >
            {showPassword ? <EyeOff size={18} /> : <Eye size={18} />}
          </button>
        </div>

        <button
          type="submit"
          className="w-full bg-gradient-to-r from-purple-500 to-indigo-500 text-white py-2 rounded-lg font-semibold hover:from-purple-600 hover:to-indigo-600 transition duration-200"
        >
          Login
        </button>

        <p className="text-sm mt-5 text-center">
          New here?{' '}
          <Link to="/register" className="text-purple-700 font-medium hover:underline">
            Create an account
          </Link>
        </p>
      </form>
    </div>
  );
}
