import { useEffect, useState, useContext } from 'react';
import { Link } from 'react-router-dom';
import API from '../api/axios';
import { AuthContext } from '../context/AuthContext';

export default function Dashboard() {
  const { user } = useContext(AuthContext);
  const [tasks, setTasks] = useState([]);
  const [users, setUsers] = useState([]);
  const [filterStatus, setFilterStatus] = useState('');
  const [filterPriority, setFilterPriority] = useState('');
  const [sortByDate, setSortByDate] = useState(false);
  const [error, setError] = useState('');

  const fetchTasks = async () => {
    try {
      const token = localStorage.getItem('token');
      const res = await API.get('/tasks', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setTasks(res.data); // ✅ Correct
      setError('');
    } catch (err) {
      console.error('❌ Fetch error:', err);
      setError('Failed to fetch tasks');
    }
  };

  const fetchUsers = async () => {
    try {
      const res = await API.get('/users');
      setUsers(res.data);
    } catch (err) {
      console.error('Failed to fetch users');
    }
  };

  useEffect(() => {
    fetchTasks();
    if (user?.role === 'admin') {
      fetchUsers();
    }
  }, []);

  const handleDelete = async (id) => {
    if (!window.confirm('Are you sure you want to delete this task?')) return;
    try {
      await API.delete(`/tasks/${id}`);
      fetchTasks();
    } catch (err) {
      alert('Error deleting task');
    }
  };

  const filteredTasks = tasks
    .filter(task =>
      (user?.role === 'admin' || task.assignedTo?._id === user?._id) &&
      (!filterStatus || task.status === filterStatus) &&
      (!filterPriority || task.priority === filterPriority)
    )
    .sort((a, b) => {
      if (!sortByDate) return 0;
      return new Date(a.dueDate) - new Date(b.dueDate);
    });

  return (
    <div className="p-6 max-w-7xl mx-auto bg-white shadow rounded">
      <h2 className="text-3xl font-bold mb-6 text-center">📋 Dashboard</h2>

      {user?.role === 'admin' && (
        <>
          {/* Admin Panel */}
          <div className="mb-10 border border-blue-200 p-4 rounded shadow bg-blue-50">
            <h3 className="text-xl font-semibold mb-2 text-blue-800">👑 Admin Panel</h3>
            <div className="flex gap-4">
              <Link to="/admin/users" className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700">
                Manage Users
              </Link>
              <Link to="/admin/users/create" className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700">
                + Add User
              </Link>
            </div>
          </div>

          {/* Admin View: Users Table */}
          <div className="mb-10">
            <h3 className="text-xl font-semibold mb-2 text-gray-800">👥 Users</h3>
            <div className="overflow-x-auto">
              <table className="w-full table-auto text-left border">
                <thead className="bg-gray-200">
                  <tr>
                    <th className="px-4 py-2">Email</th>
                    <th className="px-4 py-2">Role</th>
                    <th className="px-4 py-2">Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {users.map(user => (
                    <tr key={user._id} className="border-t hover:bg-gray-50">
                      <td className="px-4 py-2">{user.email}</td>
                      <td className="px-4 py-2 capitalize">{user.role}</td>
                      <td className="px-4 py-2 space-x-2">
                        <Link to={`/users/edit/${user._id}`} className="text-yellow-600 hover:underline">Edit</Link>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
              {users.length === 0 && (
                <p className="text-center py-4 text-gray-500">No users found</p>
              )}
            </div>
          </div>
        </>
      )}

      {/* TASK SECTION */}
      <div className="mb-6">
        <h3 className="text-xl font-semibold mb-2 text-gray-800">📝 Tasks</h3>

        <div className="flex flex-wrap gap-4 mb-4">
          <select
            onChange={e => setFilterStatus(e.target.value)}
            className="border px-3 py-2 rounded"
          >
            <option value="">Filter by Status</option>
            <option value="pending">Pending</option>
            <option value="in-progress">In Progress</option>
            <option value="completed">Completed</option>
          </select>

          <select
            onChange={e => setFilterPriority(e.target.value)}
            className="border px-3 py-2 rounded"
          >
            <option value="">Filter by Priority</option>
            <option value="low">Low</option>
            <option value="medium">Medium</option>
            <option value="high">High</option>
          </select>

          <button
            onClick={() => setSortByDate(prev => !prev)}
            className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
          >
            {sortByDate ? 'Clear Sort' : 'Sort by Due Date'}
          </button>

          {user?.role === 'admin' && (
            <Link
              to="/task/create"
              className="ml-auto bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700"
            >
              + New Task
            </Link>
          )}
        </div>

        {error && <p className="text-red-500">{error}</p>}

        <div className="overflow-x-auto">
          <table className="w-full table-auto text-left border">
            <thead className="bg-gray-200">
              <tr>
                <th className="px-4 py-2">Title</th>
                <th className="px-4 py-2">Priority</th>
                <th className="px-4 py-2">Status</th>
                <th className="px-4 py-2">Due Date</th>
                <th className="px-4 py-2">Assigned To</th>
                <th className="px-4 py-2">Actions</th>
              </tr>
            </thead>
            <tbody>
              {filteredTasks.map(task => (
                <tr key={task._id} className="border-t hover:bg-gray-50">
                  <td className="px-4 py-2">{task.title}</td>
                  <td className="px-4 py-2 capitalize">{task.priority}</td>
                  <td className="px-4 py-2 capitalize">{task.status}</td>
                  <td className="px-4 py-2">{task.dueDate?.slice(0, 10)}</td>
                  <td className="px-4 py-2">{task.assignedTo?.email || 'N/A'}</td>
                  <td className="px-4 py-2 space-x-2">
                    <Link to={`/task/${task._id}`} className="text-blue-600 hover:underline">View</Link>

                    {user?.role === 'admin' && (
                      <>
                        <Link to={`/task/edit/${task._id}`} className="text-yellow-600 hover:underline">Edit</Link>
                        <button onClick={() => handleDelete(task._id)} className="text-red-600 hover:underline">Delete</button>
                      </>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>

        {filteredTasks.length === 0 && (
          <p className="text-center py-4 text-gray-500">No tasks found</p>
        )}
      </div>
    </div>
  );
}
