import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import API from '../api/axios';

export default function CreateTask() {
  const navigate = useNavigate();
  const [form, setForm] = useState({
    title: '',
    description: '',
    status: 'pending',
    priority: 'medium',
    dueDate: ''
  });

  const [documents, setDocuments] = useState([]);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const handleChange = e => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleFileChange = e => {
    const files = Array.from(e.target.files);
    if (files.length > 3) {
      setError('Only up to 3 PDF files allowed');
      return;
    }
    setDocuments(files);
    setError('');
  };

  const handleSubmit = async e => {
    e.preventDefault();

    const formData = new FormData();
    for (let key in form) {
      formData.append(key, form[key]);
    }
    documents.forEach(doc => {
      formData.append('documents', doc);
    });

    try {
      await API.post('/tasks', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });
      setSuccess('Task created successfully!');
      navigate('/');
    } catch (err) {
      setError(err.response?.data?.msg || 'Task creation failed');
    }
  };

  return (
    <div className="p-6 max-w-xl mx-auto bg-white shadow rounded">
      <h2 className="text-2xl font-bold mb-4">Create Task</h2>

      {error && <p className="text-red-500">{error}</p>}
      {success && <p className="text-green-600">{success}</p>}

      <form onSubmit={handleSubmit} encType="multipart/form-data">
        <input
          type="text"
          name="title"
          placeholder="Title"
          onChange={handleChange}
          className="border p-2 mb-3 w-full"
          required
        />
        <textarea
          name="description"
          placeholder="Description"
          onChange={handleChange}
          className="border p-2 mb-3 w-full"
        />
        <select name="status" onChange={handleChange} className="border p-2 mb-3 w-full">
          <option value="pending">Pending</option>
          <option value="in-progress">In Progress</option>
          <option value="completed">Completed</option>
        </select>
        <select name="priority" onChange={handleChange} className="border p-2 mb-3 w-full">
          <option value="low">Low</option>
          <option value="medium" selected>Medium</option>
          <option value="high">High</option>
        </select>
        <input
          type="date"
          name="dueDate"
          onChange={handleChange}
          className="border p-2 mb-3 w-full"
          required
        />
        <input
          type="file"
          name="documents"
          multiple
          accept="application/pdf"
          onChange={handleFileChange}
          className="mb-3"
        />
        <button className="bg-blue-600 text-white px-4 py-2 w-full rounded hover:bg-blue-700">
          Create Task
        </button>
      </form>
    </div>
  );
}
