import { useEffect, useState, useContext } from 'react';
import { Link } from 'react-router-dom';
import API from '../api/axios';
import { AuthContext } from '../context/AuthContext';

export default function Dashboard() {
  const { user } = useContext(AuthContext);
  const [tasks, setTasks] = useState([]);
  const [users, setUsers] = useState([]);

  useEffect(() => {
    fetchTasks();
    if (user?.role === 'admin') fetchUsers();
  }, [user]);

  const fetchTasks = async () => {
    try {
      const res = await API.get('/api/tasks', {
        headers: { Authorization: `Bearer ${user?.token}` },
      });
      setTasks(res.data);
    } catch (err) {
      console.error('Failed to fetch tasks:', err);
    }
  };

  const fetchUsers = async () => {
    try {
      const res = await API.get('/api/users', {
        headers: { Authorization: `Bearer ${user?.token}` },
      });
      setUsers(res.data);
    } catch (err) {
      console.error('Failed to fetch users:', err);
    }
  };

  const handleDelete = async (id) => {
    try {
      await API.delete(`/api/tasks/${id}`, {
        headers: { Authorization: `Bearer ${user?.token}` },
      });
      fetchTasks();
    } catch (err) {
      console.error('Delete failed:', err);
    }
  };

  const markComplete = async (id) => {
    try {
      await API.put(
        `/api/tasks/${id}/complete`,
        {},
        {
          headers: { Authorization: `Bearer ${user?.token}` },
        }
      );
      fetchTasks();
    } catch (err) {
      console.error('Completion failed:', err);
    }
  };

  const visibleTasks =
    user?.role === 'admin'
      ? tasks
      : tasks.filter((task) => task.assignedTo === user?.id);

  return (
    <div className="p-4">
      <h2 className="text-2xl font-bold mb-4">Dashboard - {user?.role}</h2>

      {user?.role === 'admin' && (
        <div className="mb-4">
          <Link
            to="/create"
            className="bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600"
          >
            Create New Task
          </Link>
        </div>
      )}

      {visibleTasks.length === 0 ? (
        <p>No tasks found.</p>
      ) : (
        <table className="min-w-full bg-white border border-gray-300">
          <thead>
            <tr className="bg-gray-100">
              <th className="py-2 px-4 border-b">Title</th>
              <th className="py-2 px-4 border-b">Description</th>
              <th className="py-2 px-4 border-b">Assigned To</th>
              <th className="py-2 px-4 border-b">Status</th>
              <th className="py-2 px-4 border-b">Document</th>
              <th className="py-2 px-4 border-b">Actions</th>
            </tr>
          </thead>
          <tbody>
            {visibleTasks.map((task) => (
              <tr key={task._id}>
                <td className="py-2 px-4 border-b">{task.title}</td>
                <td className="py-2 px-4 border-b">{task.description}</td>
                <td className="py-2 px-4 border-b">
                  {users.find((u) => u._id === task.assignedTo)?.name ||
                    (task.assignedTo === user?.id ? 'You' : 'N/A')}
                </td>
                <td className="py-2 px-4 border-b">
                  {task.status === 'completed' ? (
                    <span className="text-green-600">Completed</span>
                  ) : (
                    <span className="text-yellow-600">Pending</span>
                  )}
                </td>
                <td className="py-2 px-4 border-b">
                  {task.document ? (
                    <a
                      href={`http://localhost:5000/${task.document}`}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="text-blue-500 underline"
                    >
                      View PDF
                    </a>
                  ) : (
                    'No file'
                  )}
                </td>
                <td className="py-2 px-4 border-b space-x-2">
                  {user?.role === 'admin' ? (
                    <>
                      <Link
                        to={`/edit/${task._id}`}
                        className="bg-yellow-500 text-white px-3 py-1 rounded"
                      >
                        Edit
                      </Link>
                      <button
                        onClick={() => handleDelete(task._id)}
                        className="bg-red-500 text-white px-3 py-1 rounded"
                      >
                        Delete
                      </button>
                    </>
                  ) : task.status !== 'completed' ? (
                    <button
                      onClick={() => markComplete(task._id)}
                      className="bg-blue-500 text-white px-3 py-1 rounded"
                    >
                      Mark Complete
                    </button>
                  ) : (
                    <span className="text-gray-400">No Action</span>
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}
