import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import API from '../api/axios';

export default function CreateTask() {
  const navigate = useNavigate();
  const [form, setForm] = useState({
    title: '',
    description: '',
    status: 'pending',
    priority: 'medium',
    dueDate: ''
  });

  const [documents, setDocuments] = useState([]);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const handleChange = e => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleFileChange = e => {
    const files = Array.from(e.target.files);
    if (files.length > 3) {
      setError('Only up to 3 PDF files allowed');
      return;
    }
    setDocuments(files);
    setError('');
  };

  const handleSubmit = async e => {
    e.preventDefault();

    const formData = new FormData();
    for (let key in form) {
      formData.append(key, form[key]);
    }
    documents.forEach(doc => {
      formData.append('documents', doc);
    });

    try {
      await API.post('/tasks', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });
      setSuccess('Task created successfully!');
      navigate('/');
    } catch (err) {
      setError(err.response?.data?.msg || 'Task creation failed');
    }
  };

  return (
    <div className="p-6 max-w-xl mx-auto bg-white shadow rounded">
      <h2 className="text-2xl font-bold mb-4 text-center">📝 Create New Task</h2>

      {error && <p className="text-red-500">{error}</p>}
      {success && <p className="text-green-600">{success}</p>}

      <form onSubmit={handleSubmit} encType="multipart/form-data" className="space-y-4">
        <div>
          <label className="block font-medium">Title</label>
          <input
            type="text"
            name="title"
            placeholder="Enter task title"
            onChange={handleChange}
            className="border p-2 w-full"
            required
          />
        </div>

        <div>
          <label className="block font-medium">Description</label>
          <textarea
            name="description"
            placeholder="Task details or notes"
            onChange={handleChange}
            className="border p-2 w-full"
          />
        </div>

        <div>
          <label className="block font-medium">Status</label>
          <select
            name="status"
            onChange={handleChange}
            className="border p-2 w-full"
          >
            <option value="pending">Pending</option>
            <option value="in-progress">In Progress</option>
            <option value="completed">Completed</option>
          </select>
        </div>

        <div>
          <label className="block font-medium">Priority</label>
          <select
            name="priority"
            onChange={handleChange}
            className="border p-2 w-full"
          >
            <option value="low">Low</option>
            <option value="medium" selected>Medium</option>
            <option value="high">High</option>
          </select>
        </div>

        <div>
          <label className="block font-medium">Due Date</label>
          <input
            type="date"
            name="dueDate"
            onChange={handleChange}
            className="border p-2 w-full"
            required
          />
        </div>

        <div>
          <label className="block font-medium">Attach PDF Documents (Max 3)</label>
          <input
            type="file"
            name="documents"
            multiple
            accept="application/pdf"
            onChange={handleFileChange}
            className="border p-2 w-full"
          />
        </div>

        <button className="bg-blue-600 text-white px-4 py-2 w-full rounded hover:bg-blue-700">
          Create Task
        </button>
      </form>
    </div>
  );
}
