import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import API from '../api/axios';

export default function CreateTask() {
  const navigate = useNavigate();
  const [form, setForm] = useState({
    title: '',
    description: '',
    status: 'pending',
    priority: 'medium',
    dueDate: ''
  });

  const [documents, setDocuments] = useState([]);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const handleChange = e => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleFileChange = e => {
    const files = Array.from(e.target.files);

    // Combine existing + new files (without exceeding 3)
    const updatedFiles = [...documents, ...files].slice(0, 3);
    setDocuments(updatedFiles);

    if (updatedFiles.length > 3) {
      setError('Only up to 3 PDF files allowed');
    } else {
      setError('');
    }

    // Clear the input so the same file can be re-selected if removed
    e.target.value = '';
  };

  const removeFile = index => {
    const updated = [...documents];
    updated.splice(index, 1);
    setDocuments(updated);
  };

  const handleSubmit = async e => {
    e.preventDefault();

    const formData = new FormData();
    for (let key in form) {
      formData.append(key, form[key]);
    }
    documents.forEach(doc => {
      formData.append('documents', doc);
    });

    try {
      await API.post('/tasks', formData, {
        headers: { 'Content-Type': 'multipart/form-data' }
      });
      setSuccess('Task created successfully!');
      navigate('/');
    } catch (err) {
      setError(err.response?.data?.msg || 'Task creation failed');
    }
  };

  return (
    <div className="p-6 max-w-xl mx-auto bg-white shadow rounded">
      <h2 className="text-2xl font-bold mb-4 text-center">üìù Create New Task</h2>

      {error && <p className="text-red-500">{error}</p>}
      {success && <p className="text-green-600">{success}</p>}

      <form onSubmit={handleSubmit} encType="multipart/form-data" className="space-y-4">
        <div>
          <label className="block font-medium">Title</label>
          <input
            type="text"
            name="title"
            placeholder="Enter task title"
            onChange={handleChange}
            className="border p-2 w-full"
            required
          />
        </div>

        <div>
          <label className="block font-medium">Description</label>
          <textarea
            name="description"
            placeholder="Task details or notes"
            onChange={handleChange}
            className="border p-2 w-full"
          />
        </div>

        <div>
          <label className="block font-medium">Status</label>
         <select
  name="status"
  value={form.status}
  onChange={handleChange}
  className="border p-2 w-full"
>
  <option value="pending">Pending</option>
  <option value="in-progress">In Progress</option>
  <option value="completed">Completed</option>
</select>

        </div>

        <div>
          <label className="block font-medium">Priority</label>
         <select
  name="priority"
  value={form.priority}
  onChange={handleChange}
  className="border p-2 w-full"
>
  <option value="low">Low</option>
  <option value="medium">Medium</option>
  <option value="high">High</option>
</select>

        </div>

        <div>
          <label className="block font-medium">Due Date</label>
          <input
            type="date"
            name="dueDate"
            onChange={handleChange}
            className="border p-2 w-full"
            required
          />
        </div>

        <div>
          <label className="block font-medium">Attach PDF Documents (Max 3)</label>
          <input
            type="file"
            accept="application/pdf"
            onChange={handleFileChange}
            className="border p-2 w-full"
          />
        </div>

        {documents.length > 0 && (
          <div className="bg-gray-100 p-2 rounded text-sm">
            <p className="font-semibold mb-1">Selected Files:</p>
            <ul className="space-y-1">
              {documents.map((file, index) => (
                <li key={index} className="flex justify-between items-center">
                  <span>{file.name}</span>
                  <button
                    type="button"
                    onClick={() => removeFile(index)}
                    className="text-red-500 text-sm hover:underline"
                  >
                    Remove
                  </button>
                </li>
              ))}
            </ul>
          </div>
        )}

        <button className="bg-blue-600 text-white px-4 py-2 w-full rounded hover:bg-blue-700">
          Create Task
        </button>
      </form>
    </div>
  );
}
