import { useEffect, useState, useContext } from 'react';
import { Link } from 'react-router-dom';
import API from '../api/axios';
import { AuthContext } from '../context/AuthContext';

export default function Dashboard() {
  const { user } = useContext(AuthContext);
  const [tasks, setTasks] = useState([]);
  const [filterStatus, setFilterStatus] = useState('all');

  useEffect(() => {
    const fetchTasks = async () => {
      try {
        const res = await API.get('/tasks');
        setTasks(res.data);
      } catch (error) {
        console.error('Failed to fetch tasks', error);
      }
    };
    fetchTasks();
  }, []);

  const handleStatusChange = async (id, newStatus) => {
    try {
      await API.put(`/tasks/${id}`, { status: newStatus });
      setTasks(prev =>
        prev.map(task => (task._id === id ? { ...task, status: newStatus } : task))
      );
    } catch (err) {
      console.error('Status update failed', err);
    }
  };

  const filteredTasks = filterStatus === 'all'
    ? tasks
    : tasks.filter(task => task.status === filterStatus);

  return (
    <div className="p-6 max-w-5xl mx-auto">
      <div className="flex justify-between items-center mb-6">
        <h2 className="text-3xl font-bold">Dashboard</h2>
        <div>
          {user.role === 'admin' && (
            <Link to="/create" className="bg-blue-600 text-white px-4 py-2 rounded mr-2">
              Create Task
            </Link>
          )}
          <select
            className="border px-3 py-2 rounded"
            value={filterStatus}
            onChange={e => setFilterStatus(e.target.value)}
          >
            <option value="all">All</option>
            <option value="pending">Pending</option>
            <option value="in_progress">In Progress</option>
            <option value="completed">Completed</option>
          </select>
        </div>
      </div>

      {filteredTasks.length === 0 ? (
        <p>No tasks found.</p>
      ) : (
        <ul className="space-y-4">
          {filteredTasks.map(task => (
            <li key={task._id} className="border p-4 rounded shadow">
              <h3 className="text-xl font-semibold">{task.title}</h3>
              <p className="text-gray-700">{task.description}</p>
              <p className="text-sm mt-1 text-gray-500">
                Status: <span className="capitalize">{task.status}</span>
              </p>
              {task.assignedTo && (
                <p className="text-sm text-gray-500">
                  Assigned to: <span className="font-medium">{task.assignedTo.email}</span>
                </p>
              )}
              <div className="mt-4 flex flex-wrap gap-2">
                {user.role === 'admin' && (
                  <>
                    <Link to={`/edit/${task._id}`} className="bg-yellow-500 text-white px-3 py-1 rounded">
                      Edit
                    </Link>
                    <button
                      onClick={async () => {
                        await API.delete(`/tasks/${task._id}`);
                        setTasks(prev => prev.filter(t => t._id !== task._id));
                      }}
                      className="bg-red-600 text-white px-3 py-1 rounded"
                    >
                      Delete
                    </button>
                  </>
                )}
                {user.role !== 'admin' && (
                  <select
                    className="border px-2 py-1 rounded"
                    value={task.status}
                    onChange={e => handleStatusChange(task._id, e.target.value)}
                  >
                    <option value="pending">Pending</option>
                    <option value="in_progress">In Progress</option>
                    <option value="completed">Completed</option>
                  </select>
                )}
              </div>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}
