import { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import API from '../api/axios';

export default function Dashboard() {
  const [tasks, setTasks] = useState([]); // âœ… Must be initialized as an array
  const [filterStatus, setFilterStatus] = useState('');
  const [filterPriority, setFilterPriority] = useState('');
  const [sortByDate, setSortByDate] = useState(false);
  const [error, setError] = useState('');

 const fetchTasks = async () => {
  try {
    const res = await API.get('/tasks');
    console.log("Fetched tasks:", res.data); // â¬…ï¸ Check this
    setTasks(res.data.tasks);
  // Only if res.data is an array
  } catch (err) {
    setError('Failed to fetch tasks');
  }
};


  useEffect(() => {
    fetchTasks();
  }, []);

  const handleDelete = async (id) => {
    if (!window.confirm('Are you sure you want to delete this task?')) return;
    try {
      await API.delete(`/tasks/${id}`);
      fetchTasks();
    } catch (err) {
      alert('Error deleting task');
    }
  };

  // Apply filters/sort
  const filteredTasks = tasks
    .filter(t =>
      (!filterStatus || t.status === filterStatus) &&
      (!filterPriority || t.priority === filterPriority)
    )
    .sort((a, b) => {
      if (!sortByDate) return 0;
      return new Date(a.dueDate) - new Date(b.dueDate);
    });

  return (
    <div className="p-6 max-w-5xl mx-auto bg-white shadow rounded">
      <h2 className="text-2xl font-bold mb-4 text-center">ðŸ“‹ Dashboard</h2>

      <div className="flex flex-wrap gap-4 mb-4">
        <select
          onChange={e => setFilterStatus(e.target.value)}
          className="border px-3 py-2 rounded"
        >
          <option value="">Filter by Status</option>
          <option value="pending">Pending</option>
          <option value="in-progress">In Progress</option>
          <option value="completed">Completed</option>
        </select>

        <select
          onChange={e => setFilterPriority(e.target.value)}
          className="border px-3 py-2 rounded"
        >
          <option value="">Filter by Priority</option>
          <option value="low">Low</option>
          <option value="medium">Medium</option>
          <option value="high">High</option>
        </select>

        <button
          onClick={() => setSortByDate(prev => !prev)}
          className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
        >
          {sortByDate ? 'Clear Sort' : 'Sort by Due Date'}
        </button>

        <Link
          to="/task/create"
          className="ml-auto bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700"
        >
          + New Task
        </Link>
      </div>

      {error && <p className="text-red-500">{error}</p>}

      <div className="overflow-x-auto">
        <table className="w-full table-auto text-left border">
          <thead className="bg-gray-200">
            <tr>
              <th className="px-4 py-2">Title</th>
              <th className="px-4 py-2">Priority</th>
              <th className="px-4 py-2">Status</th>
              <th className="px-4 py-2">Due Date</th>
              <th className="px-4 py-2">Actions</th>
            </tr>
          </thead>
          <tbody>
            {filteredTasks.map(task => (
              <tr key={task._id} className="border-t hover:bg-gray-50">
                <td className="px-4 py-2">{task.title}</td>
                <td className="px-4 py-2 capitalize">{task.priority}</td>
                <td className="px-4 py-2 capitalize">{task.status}</td>
                <td className="px-4 py-2">{task.dueDate?.slice(0, 10)}</td>
                <td className="px-4 py-2 space-x-2">
                  <Link
                    to={`/task/${task._id}`}
                    className="text-blue-600 hover:underline"
                  >
                    View
                  </Link>
                  <Link
                    to={`/task/edit/${task._id}`}
                    className="text-yellow-600 hover:underline"
                  >
                    Edit
                  </Link>
                  <button
                    onClick={() => handleDelete(task._id)}
                    className="text-red-600 hover:underline"
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {filteredTasks.length === 0 && (
        <p className="text-center py-4 text-gray-500">No tasks found</p>
      )}
    </div>
  );
}
