import { useEffect, useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import API from '../api/axios';

export default function TaskDetail() {
  const { id } = useParams();
  const [task, setTask] = useState(null);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchTask = async () => {
      try {
        const token = localStorage.getItem('token');
        const res = await API.get(`/tasks/${id}`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setTask(res.data);
      } catch (err) {
        console.error('❌ Error fetching task detail:', err.response?.data || err.message);
        setError('Could not fetch task details');
      }
    };
    fetchTask();
  }, [id]);

  if (error) {
    return <div className="p-6 text-red-500 text-center">{error}</div>;
  }

  if (!task) {
    return <div className="p-6 text-center">Loading task...</div>;
  }

  return (
    <div className="p-6 max-w-3xl mx-auto bg-white shadow rounded">
      <h2 className="text-2xl font-bold mb-4">Task Details</h2>

      <div className="space-y-2">
        <p><span className="font-semibold">Title:</span> {task.title}</p>
        <p><span className="font-semibold">Description:</span> {task.description || 'N/A'}</p>
        <p><span className="font-semibold">Status:</span> {task.status}</p>
        <p><span className="font-semibold">Priority:</span> {task.priority}</p>
        <p><span className="font-semibold">Due Date:</span> {task.dueDate?.slice(0, 10)}</p>
        <p><span className="font-semibold">Assigned To:</span> {task.assignedTo?.email || 'N/A'}</p>
        <p><span className="font-semibold">Created:</span> {new Date(task.createdAt).toLocaleString()}</p>
      </div>

      <div className="mt-6">
        <h3 className="font-semibold mb-2">Attached Documents:</h3>
        {task.documents.length === 0 ? (
          <p>No documents attached.</p>
        ) : (
          <ul className="space-y-2">
            {task.documents.map((doc, index) => (
              <li key={index}>
                <a
                  href={`http://localhost:5000/${doc}`}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="text-blue-600 hover:underline"
                >
                  📄 View Document {index + 1}
                </a>
              </li>
            ))}
          </ul>
        )}
      </div>

      <div className="mt-6">
        <Link
          to="/"
          className="inline-block bg-gray-600 text-white px-4 py-2 rounded hover:bg-gray-700"
        >
          ← Back to Dashboard
        </Link>
      </div>
    </div>
  );
}
