import { useEffect, useState, useContext } from 'react';
import { Link, useLocation } from 'react-router-dom';
import API from '../api/axios';
import { AuthContext } from '../context/AuthContext';
import Navbar from '../components/Navbar';

export default function Dashboard() {
  const { user } = useContext(AuthContext);
  const location = useLocation();
  const [tasks, setTasks] = useState([]);
  const [users, setUsers] = useState([]);
  const [filterStatus, setFilterStatus] = useState('');
  const [filterPriority, setFilterPriority] = useState('');
  const [sortByDate, setSortByDate] = useState(false);
  const [error, setError] = useState('');
  const [assignModal, setAssignModal] = useState({ show: false, userId: null });
  const [selectedTaskId, setSelectedTaskId] = useState('');
  const [page, setPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [userPage, setUserPage] = useState(1);
  const USERS_PER_PAGE = 5;

  const fetchTasks = async () => {
    try {
      const token = localStorage.getItem('token');
      const res = await API.get(`/tasks?page=${page}&limit=5`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      const fetchedTasks = res.data.tasks || [];
      setTasks(fetchedTasks);
      setTotalPages(res.data.totalPages || 1);
      setError('');
    } catch (err) {
      console.error('❌ Fetch error:', err);
      setError('Failed to fetch tasks');
    }
  };

  const fetchUsers = async () => {
    try {
      const token = localStorage.getItem('token');
      const res = await API.get('/users', {
        headers: { Authorization: `Bearer ${token}` },
      });
      setUsers(res.data);
    } catch (err) {
      console.error('Failed to fetch users');
    }
  };

  useEffect(() => {
    fetchTasks();
    if (user?.role === 'admin') fetchUsers();
  }, [user?.role, page]);

  const handleDelete = async (id) => {
    if (!window.confirm('Are you sure you want to delete this task?')) return;
    try {
      const token = localStorage.getItem('token');
      await API.delete(`/tasks/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      fetchTasks();
    } catch (err) {
      alert('Error deleting task');
    }
  };

  const handleAssign = async () => {
    try {
      const token = localStorage.getItem('token');
      await API.put(
        `/tasks/${selectedTaskId}/assign`,
        { userId: assignModal.userId },
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      fetchTasks();
      setAssignModal({ show: false, userId: null });
      setSelectedTaskId('');
    } catch (err) {
      alert('Failed to assign task');
    }
  };

  const handleStatusChange = async (taskId, newStatus) => {
    try {
      const token = localStorage.getItem('token');
      await API.put(
        `/tasks/${taskId}/status`,
        { status: newStatus },
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      fetchTasks();
    } catch (err) {
      console.error('Status update failed:', err);
      alert('Failed to update status');
    }
  };

  const filteredTasks = tasks
    .filter(
      (t) =>
        (!filterStatus || t.status === filterStatus) &&
        (!filterPriority || t.priority === filterPriority)
    )
    .sort((a, b) => (sortByDate ? new Date(a.dueDate) - new Date(b.dueDate) : 0));

  const paginatedUsers = users.filter((u) => u.role !== 'admin');
  const totalUserPages = Math.ceil(paginatedUsers.length / USERS_PER_PAGE);
  const currentPageUsers = paginatedUsers.slice(
    (userPage - 1) * USERS_PER_PAGE,
    userPage * USERS_PER_PAGE
  );

  return (
    <>
      <Navbar />
      <div className="flex min-h-[calc(100vh-64px)]">
        <aside className="w-64 bg-gray-100 border-r p-4 hidden md:block">
          <h3 className="text-xl font-bold mb-4 text-indigo-700">📁 Navigation</h3>
          <nav className="flex flex-col gap-2">
            <Link to="/" className="hover:bg-indigo-200 px-4 py-2 rounded">
              🏠 Dashboard
            </Link>
            {user?.role === 'admin' && (
              <>
                <Link to="/task/create" className="hover:bg-indigo-200 px-4 py-2 rounded">
                  ➕ Create Task
                </Link>
                <Link to="/admin/users" className="hover:bg-indigo-200 px-4 py-2 rounded">
                  👥 Manage Users
                </Link>
                <Link to="/admin/users/create" className="hover:bg-indigo-200 px-4 py-2 rounded">
                  ➕ Add User
                </Link>
              </>
            )}
          </nav>
        </aside>

        <main className="flex-1 p-4 sm:p-6 bg-white overflow-x-auto">
          <h2 className="text-2xl sm:text-3xl font-bold mb-6 text-center">📋 Dashboard</h2>

          {user?.role === 'admin' && (
            <div className="mb-10">
              <h3 className="text-xl font-semibold mb-4">👤 Users</h3>
              <div className="overflow-x-auto rounded border">
                <table className="w-full text-sm">
                  <thead className="bg-gray-100">
                    <tr>
                      <th className="px-4 py-2 text-left">Email</th>
                      <th className="px-4 py-2 text-left">Role</th>
                      <th className="px-4 py-2 text-left">Actions</th>
                    </tr>
                  </thead>
                  <tbody>
                    {currentPageUsers.map((u) => (
                      <tr key={u._id} className="border-t hover:bg-gray-50">
                        <td className="px-4 py-2">{u.email}</td>
                        <td className="px-4 py-2 capitalize">{u.role}</td>
                        <td className="px-4 py-2 space-x-2">
                          <Link
                            to={`/users/edit/${u._id}`}
                            className="text-yellow-600 hover:underline"
                          >
                            Edit
                          </Link>
                          <button
                            className="text-indigo-600 hover:underline"
                            onClick={() => setAssignModal({ show: true, userId: u._id })}
                          >
                            Assign
                          </button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
              {totalUserPages > 1 && (
                <div className="flex justify-center mt-4 gap-2">
                  <button
                    onClick={() => setUserPage((p) => Math.max(1, p - 1))}
                    disabled={userPage === 1}
                    className="px-3 py-1 bg-gray-200 rounded"
                  >
                    Prev
                  </button>
                  {[...Array(totalUserPages)].map((_, i) => (
                    <button
                      key={i}
                      onClick={() => setUserPage(i + 1)}
                      className={`px-3 py-1 rounded ${
                        userPage === i + 1 ? 'bg-indigo-600 text-white' : 'bg-gray-100'
                      }`}
                    >
                      {i + 1}
                    </button>
                  ))}
                  <button
                    onClick={() => setUserPage((p) => Math.min(totalUserPages, p + 1))}
                    disabled={userPage === totalUserPages}
                    className="px-3 py-1 bg-gray-200 rounded"
                  >
                    Next
                  </button>
                </div>
              )}
            </div>
          )}

          {/* ...Task Section stays unchanged from your previous code... */}
      

          {/* 📋 Task Section */}
          <div className="mb-6">
            <h3 className="text-xl font-semibold mb-4 text-gray-700">📝 Tasks</h3>

            <div className="flex flex-wrap gap-4 mb-4">
              <select
                onChange={(e) => setFilterStatus(e.target.value)}
                className="border px-3 py-2 rounded"
              >
                <option value="">Filter by Status</option>
                <option value="pending">Pending</option>
                <option value="in-progress">In Progress</option>
                <option value="completed">Completed</option>
              </select>

              <select
                onChange={(e) => setFilterPriority(e.target.value)}
                className="border px-3 py-2 rounded"
              >
                <option value="">Filter by Priority</option>
                <option value="low">Low</option>
                <option value="medium">Medium</option>
                <option value="high">High</option>
              </select>

              <button
                onClick={() => setSortByDate((prev) => !prev)}
                className="bg-indigo-600 text-white px-4 py-2 rounded hover:bg-indigo-700"
              >
                {sortByDate ? 'Clear Sort' : 'Sort by Due Date'}
              </button>
            </div>

            {error && <p className="text-red-500">{error}</p>}

            <div className="overflow-x-auto rounded border">
              <table className="w-full table-auto text-left">
                <thead className="bg-gray-100">
                  <tr>
                    <th className="px-4 py-2">Title</th>
                    <th className="px-4 py-2">Priority</th>
                    <th className="px-4 py-2">Status</th>
                    <th className="px-4 py-2">Due Date</th>
                    <th className="px-4 py-2">Assigned To</th>
                    <th className="px-4 py-2">Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {filteredTasks.map((task) => (
                    <tr key={task._id} className="border-t hover:bg-gray-50">
                      <td className="px-4 py-2">{task.title}</td>
                      <td className="px-4 py-2 capitalize">{task.priority}</td>

                      {/* 🔁 Editable Status by Assigned User */}
                      <td className="px-4 py-2 capitalize">
                        {user?.email === task.assignedTo?.email ? (
                          <select
                            value={task.status}
                            onChange={(e) => handleStatusChange(task._id, e.target.value)}
                            className="border rounded px-2 py-1"
                          >
                            <option value="pending">Pending</option>
                            <option value="in-progress">In Progress</option>
                            <option value="completed">Completed</option>
                          </select>
                        ) : (
                          task.status
                        )}
                      </td>

                      <td className="px-4 py-2">{task.dueDate?.slice(0, 10)}</td>
                      <td className="px-4 py-2">{task.assignedTo?.email || 'N/A'}</td>
                      <td className="px-4 py-2 space-x-2">
                        <Link to={`/task/${task._id}`} className="text-blue-600 hover:underline">
                          View
                        </Link>
                        {user?.role === 'admin' && (
                          <>
                            <Link
                              to={`/task/edit/${task._id}`}
                              className="text-yellow-600 hover:underline"
                            >
                              Edit
                            </Link>
                            <button
                              onClick={() => handleDelete(task._id)}
                              className="text-red-600 hover:underline"
                            >
                              Delete
                            </button>
                          </>
                        )}
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>

            {filteredTasks.length === 0 && (
              <p className="text-center py-4 text-gray-500">No tasks found</p>
            )}

            {/* 🔁 Pagination */}
            {totalPages > 1 && (
              <div className="flex justify-center gap-2 mt-4">
                <button
                  onClick={() => setPage((p) => Math.max(1, p - 1))}
                  disabled={page === 1}
                  className="px-3 py-1 bg-gray-200 rounded disabled:opacity-50"
                >
                  Prev
                </button>
                {[...Array(totalPages)].map((_, i) => (
                  <button
                    key={i}
                    onClick={() => setPage(i + 1)}
                    className={`px-3 py-1 rounded ${
                      page === i + 1 ? 'bg-indigo-600 text-white' : 'bg-gray-100'
                    }`}
                  >
                    {i + 1}
                  </button>
                ))}
                <button
                  onClick={() => setPage((p) => Math.min(totalPages, p + 1))}
                  disabled={page === totalPages}
                  className="px-3 py-1 bg-gray-200 rounded disabled:opacity-50"
                >
                  Next
                </button>
              </div>
            )}
          </div>
        </main>
      </div>

      {/* ✅ Assign Modal */}
      {assignModal.show && (
        <div className="fixed inset-0 bg-black bg-opacity-40 flex items-center justify-center z-50">
          <div className="bg-white p-6 rounded-lg shadow-xl w-96">
            <h3 className="text-lg font-semibold mb-4">Assign Task to User</h3>
            <select
              value={selectedTaskId}
              onChange={(e) => setSelectedTaskId(e.target.value)}
              className="w-full border px-3 py-2 rounded mb-4"
            >
              <option value="">Select a task</option>
              {tasks.map((task) => (
                <option key={task._id} value={task._id}>
                  {task.title}
                </option>
              ))}
            </select>
            <div className="flex justify-end gap-2">
              <button
                onClick={() => setAssignModal({ show: false, userId: null })}
                className="bg-gray-300 px-4 py-2 rounded"
              >
                Cancel
              </button>
              <button
                onClick={handleAssign}
                disabled={!selectedTaskId}
                className="bg-indigo-600 text-white px-4 py-2 rounded hover:bg-indigo-700"
              >
                Assign
              </button>
            </div>
          </div>
        </div>
      )}
    </>
  );
}
