// Dashboard.jsx
import { useEffect, useState, useContext } from 'react';
import { Link, useLocation } from 'react-router-dom';
import API from '../api/axios';
import { AuthContext } from '../context/AuthContext';
import Navbar from '../components/Navbar';

export default function Dashboard() {
  const { user } = useContext(AuthContext);
  const location = useLocation();
  const [tasks, setTasks] = useState([]);
  const [users, setUsers] = useState([]);
  const [error, setError] = useState('');

  const fetchTasks = async () => {
    try {
      const token = localStorage.getItem('token');
      const res = await API.get('/tasks', {
        headers: { Authorization: `Bearer ${token}` },
      });
      setTasks(res.data.tasks || []);
      setError('');
    } catch (err) {
      console.error('❌ Fetch error:', err);
      setError('Failed to fetch tasks');
    }
  };

  const fetchUsers = async () => {
    try {
      const res = await API.get('/users');
      setUsers(res.data);
    } catch (err) {
      console.error('Failed to fetch users');
    }
  };

  useEffect(() => {
    fetchTasks();
    if (user?.role === 'admin') fetchUsers();
  }, []);

  return (
    <>
      <Navbar />
      <div className="flex min-h-[calc(100vh-64px)]">
        {/* Sidebar */}
        <aside className="w-60 bg-gray-100 border-r p-6 hidden md:block">
          <h3 className="text-xl font-semibold mb-6 text-indigo-700">📂 Admin Menu</h3>
          <ul className="space-y-4">
            <li>
              <Link to="/" className={location.pathname === '/' ? active : link}>🏠 Dashboard</Link>
            </li>
            <li>
              <h4 className="text-gray-600 font-semibold">👥 Users</h4>
              <ul className="ml-4 space-y-2">
                <li><Link to="/admin/users" className={location.pathname.includes('/admin/users') ? active : link}>All Users</Link></li>
                <li><Link to="/admin/users/create" className={location.pathname.includes('/admin/users/create') ? active : link}>Manage Users</Link></li>
              </ul>
            </li>
            <li>
              <h4 className="text-gray-600 font-semibold">📝 Tasks</h4>
              <ul className="ml-4 space-y-2">
                <li><Link to="/tasks/all" className={location.pathname.includes('/tasks/all') ? active : link}>All Tasks</Link></li>
                <li><Link to="/task/create" className={location.pathname.includes('/task/create') ? active : link}>Manage Tasks</Link></li>
              </ul>
            </li>
          </ul>
        </aside>

        {/* Content Area */}
        <main className="flex-1 p-6 bg-white overflow-x-auto">
          <h2 className="text-3xl font-bold mb-6 text-center text-gray-800">📋 Admin Dashboard</h2>

          {/* USERS CARD VIEW */}
          <div className="mb-10">
            <h3 className="text-xl font-semibold mb-4 text-gray-700">👥 All Users</h3>
            {users.length ? (
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                {users.map(u => (
                  <div key={u._id} className="border rounded p-4 shadow-sm">
                    <p><span className="font-semibold">Email:</span> {u.email}</p>
                    <p><span className="font-semibold">Role:</span> {u.role}</p>
                    <Link to={`/users/edit/${u._id}`} className="text-indigo-600 mt-2 inline-block hover:underline">Edit</Link>
                  </div>
                ))}
              </div>
            ) : (
              <p className="text-center py-4 text-gray-500">No users found</p>
            )}
          </div>

          {/* TASK TABLE */}
          <div className="mb-6">
            <h3 className="text-xl font-semibold mb-4 text-gray-700">📝 All Tasks</h3>
            {error && <p className="text-red-500">{error}</p>}
            <div className="overflow-x-auto rounded border">
              <table className="w-full table-auto text-left">
                <thead className="bg-gray-100">
                  <tr>
                    <th className="px-4 py-2">Title</th>
                    <th className="px-4 py-2">Priority</th>
                    <th className="px-4 py-2">Status</th>
                    <th className="px-4 py-2">Due Date</th>
                    <th className="px-4 py-2">Assigned To</th>
                    <th className="px-4 py-2">Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {tasks.map(task => (
                    <tr key={task._id} className="border-t hover:bg-gray-50">
                      <td className="px-4 py-2">{task.title}</td>
                      <td className="px-4 py-2 capitalize">{task.priority}</td>
                      <td className="px-4 py-2 capitalize">{task.status}</td>
                      <td className="px-4 py-2">{task.dueDate?.slice(0, 10)}</td>
                      <td className="px-4 py-2">{task.assignedTo?.email || 'N/A'}</td>
                      <td className="px-4 py-2 space-x-2">
                        <Link to={`/task/${task._id}`} className="text-blue-600 hover:underline">View</Link>
                        <Link to={`/task/edit/${task._id}`} className="text-yellow-600 hover:underline">Edit</Link>
                        <button onClick={() => handleDelete(task._id)} className="text-red-600 hover:underline">Delete</button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        </main>
      </div>
    </>
  );
}

const link = 'block px-3 py-2 rounded hover:bg-indigo-100';
const active = 'block px-3 py-2 rounded bg-indigo-200 text-indigo-800 font-semibold';
